mapper.update:
UPDATE borrow 
	SET bank_a = #{bankA}
	   ,bank_b = #{bankB}
	WHERE bid=#{bid}

UPDATE borrow 
	SET pay_a = #{payA}
	   ,pay_b = #{payB}
	   ,pay_c = #{payC}
	   ,pay_d = #{payD}
	WHERE bid=#{bid}

UPDATE borrow 
	SET operator_phone = #{operatorPhone}   
	   ,operator_idcard =#{operatorIdcard}
	   ,operator_name = #{operatorName}
	   ,is_agreement = #{isAgreement}
	WHERE bid=#{bid}
